sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	// Instantiate the GetEmailAddress Script Include
	var addThreeDays = new GlideAjax('DueDatePlusThree');
	// Specify the AddThreeDays method
	addThreeDays.addParam('sysparm_name','AddThreeDays');
	// Send the request to the server
	addThreeDays.getXML(populateDueDateField);
	// When the response is back from the server
	function populateDueDateField(response){
		//Extract the Due date from the response, clear any value from the Due date field,
		//set new value to the Due date field
		var dueDateFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		g_form.setValue('due_date',dueDateFromScriptInclude);
	}

}
