sys_script_include.script:
var DueDatePlusThree = Class.create();
DueDatePlusThree.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	//Define the AddThreeDays function
	//Create a object of GlideDateTime to get the today's date
	//Plus 3 days to the today's date object and return it
	AddThreeDays: function(){
		var date = new GlideDateTime();
		date.addDaysLocalTime(3);
		var time = date.getLocalTime();
		var dueDate = (date.getLocalDate() + " " + time.getByFormat('HH:mm:ss'));
		return dueDate;
	},
    type: 'DueDatePlusThree'
});
