sys_script_include.script:
var GenerateUserIDScript = Class.create();
GenerateUserIDScript.prototype = {
	// The next function generates the UserID
    generateUserId: function(strFirstLetter,strLastName) {
		// Take the first letter of the First name field
		// Take the value of the Last name field
		// Set the value to the UserID field
		var first_name = strFirstLetter.charAt(0);
		var user_id = (first_name + strLastName).toLowerCase();
		return user_id;
    },

    type: 'GenerateUserIDScript'
};
