sysauto_script.script:
// Get today's date and obtain the month 
var today = new GlideDateTime();
// Incidents records has an updated field
// compare if there are 3 or more months without activity
var incidentRecords = new GlideRecord('incident');
incidentRecords.addQuery('active','=',true);
incidentRecords.addQuery('state','=',2);
incidentRecords.addQuery('sys_updated_on','<=',today);
incidentRecords.query();

// Change Active field to false
while(incidentRecords.next()){
	gs.info("Incident: " + incidentRecords.number);
	gs.eventQueue('x_58872_needit.inactiveIncident',incidentRecords,incidentRecords.number,gs.getUserName());
}
